(function() {
  const fullText = outputs.appMentionSubscription.text;

  // The mention format is usually <@APP_ID> with a space after it
  // We'll find the first occurrence of ">" and take everything after that
  const mentionEndIndex = fullText.indexOf('>');

  if (mentionEndIndex !== -1) {
    // Extract everything after the mention, trim any leading whitespace
    return fullText.substring(mentionEndIndex + 1).trim();
  } else {
    // If no mention pattern is found, return the original text
    return fullText;
  }
}())

